SELECT * FROM person."work_experiences"
WHERE (
    "id" ilike '%{{data_table.searchText || ""}}%' 
    OR "id" = '{{data_table.searchText || ""}}'
    OR "company_id" ilike '%{{data_table.searchText || ""}}%'
    OR "company_id" = '{{data_table.searchText || ""}}'
    OR "resume_detail" ilike '%{{data_table.searchText || ""}}%'
) 
AND "talent_id" = '{{talent_table.selectedRow.id}}' 
AND deleted_at is Null
ORDER BY "{{data_table.sortOrder.column || 'index'}}" {{data_table.sortOrder.order || 'ASC'}}
LIMIT {{data_table.pageSize}}
OFFSET {{(data_table.pageNo - 1) * data_table.pageSize}};
